import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_URL = "https://api.openai.com/v1/chat/completions";
const apiKey = process.env.OPENAI_API_KEY;
const config = {
  headers: {
    "Content-Type": "application/json",
    Authorization: `Bearer ${apiKey}`,
  },
};

function App() {
  const [inputText, setInputText] = useState('');
  const [placeholder, setPlaceholder] = useState('Type something...');
  const [conversation, setConversation] = useState([{ role: 'ai', content: "Merhaba! Size nasıl yardımcı olabilirim?" }]);

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  const handleSubmit = () => {
    if (inputText.trim() !== '') {
      setConversation(prevConversation => [
        ...prevConversation,
        { role: 'user', content: inputText }
      ]);

      axios.post(API_URL, { model: "gpt-3.5-turbo", messages: conversation }, config)
        .then((response) => {
          setConversation(prevConversation => [
            ...prevConversation,
            { role: 'ai', content: response.data.choices[0].message.content }
          ]);
        })
        .catch((error) => {
          console.log(error);
        });

      setInputText('');
      setPlaceholder('Type something...');
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSubmit();
    }
  };

  const handleFocus = () => {
    if (inputText.trim() === '') {
      setPlaceholder('');
    }
  };

  const handleBlur = () => {
    if (inputText.trim() === '') {
      setPlaceholder('Type something...');
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="image"></div>
        <div className="conversation">
          {conversation.map((message, index) => (
            <div key={index} className={message.role === 'user' ? 'user-message' : 'ai-message'}>
              {message.content}
            </div>
          ))}
        </div>
        <input
          className='text'
          type="text"
          value={inputText}
          onChange={handleInputChange}
          onFocus={handleFocus}
          onBlur={handleBlur}
          onKeyPress={handleKeyPress}
          placeholder={placeholder}
        />
        <div className="box"></div>
        <button className="super-mario-button" onClick={handleSubmit}>SUBMIT</button>
        <div className="character"></div>
        <div className="cloud cloud1"></div>
        <div className="cloud cloud2"></div>
        <div className="cloud cloud3"></div>
        <div className="cloud cloud4"></div>
      </header>
    </div>
  );
}

export default App;
