import React, { useState, useEffect, ChangeEvent, KeyboardEvent } from 'react';
import './App.css';

function App() {
  const [inputText, setInputText] = useState<string>('');
  const [placeholder, setPlaceholder] = useState<string>('Type something...');

  const handleInputChange = (event: ChangeEvent<HTMLInputElement>) => {
    setInputText(event.target.value);
  };

  const handleSubmit = () => {
    if (inputText.trim() !== '') {
      console.log(inputText);
      setInputText('');
      setPlaceholder('Type something...');
    }
  };

  const handleKeyPress = (event: KeyboardEvent<HTMLInputElement>) => {
    if (event.key === 'Enter') {
      handleSubmit();
    }
  };

  const handleFocus = () => {
    if (inputText.trim() === '') {
      setPlaceholder('');
    }
  };

  const handleBlur = () => {
    if (inputText.trim() === '') {
      setPlaceholder('Type something...');
    }
  };

  const [isMusicPlaying, setIsMusicPlaying] = useState(false);

  const toggleMusic = () => {
    setIsMusicPlaying((prevState) => !prevState);
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="image"></div>
        <input
          className="text"
          type="text"
          value={inputText}
          onChange={handleInputChange}
          onFocus={handleFocus}
          onBlur={handleBlur}
          onKeyPress={handleKeyPress}
          placeholder={placeholder}
        />
        <input className="answer" type="text" placeholder={'Answer will be here...'} />
        <div className="box"></div>
        <button className="super-mario-button" onClick={handleSubmit}>
          SUBMIT
        </button>

        {/* Mario karakteri */}
        <div className="character"></div>

        {/* Bulutlar */}
        <div className="cloud cloud1"></div>
        <div className="cloud cloud2"></div>
        <div className="cloud cloud3"></div>
        <div className="cloud cloud4"></div>

        {/* Engeller */}

        <button className="music-button" onClick={toggleMusic}>
          {isMusicPlaying ? 'Müziği Sessize Al' : 'Müziği Aç'}
        </button>

        {/* Müzik */}
        {isMusicPlaying && (
          <audio src="/Users/berkakaz/Documents/GitHub/AIProject/my-app/public/Super Mario Bros. medley.mp3" autoPlay loop />
        )}
      </header>
    </div>
  );
}

export default App;
